Create View [GUSTO00001].[VG_GelirGiderHareketBaglanti] As Select pIslemID, sBelgeSeri as seri, lBelgeNo as no from GUSTO00001.BankaHareket union select pIslemID, sSeri as seri, nBelgeNo as no from GUSTO00001.CariHareket  union select pIslemID, sSeri as seri, nBelgeNo as no from GUSTO00001.DigerHareket union select pIslemID, sBelgeSeri as seri, lBelgeNo as no from GUSTO00001.KasaHareket union select h.pIslemID, k.sSeri as seri, k.lSeriNo as no from GUSTO00001.verilencekkart k inner join GUSTO00001.verilencekhareket h on k.verilencekkart_rowid=h.pcek union select ah.pIslemID, ak.sSeri as seri, ak.lSeriNo as no from GUSTO00001.alinancekkart ak inner join GUSTO00001.alinancekhareket ah on ak.alinancekkart_rowid=ah.pcek
GO