SET QUOTED_IDENTIFIER, ANSI_NULLS OFF
GO
CREATE view [GUSTO00001].[V_Ekkesinti] as select nYil, sekkesintiadi, nAy,pek.pPersonel,pek.pIsYeri,pek.pIkramiyeTanimi, pek.ldBruttutar from GUSTO00001.PersonelEkGelirKesinti pek inner join GUSTO00001.EkKesintiTanimi ek on pek.pekGelirKesinti=ek.ekKesintiTanimi_rowid where pek.ntip=1 and baliyor=1 and  pek.ldBruttutar>0 union all select nYil, ek.sAd, nAy,pek.pPersonel,pek.pIsYeri,pek.pIkramiyeTanimi, pek.ldAvansTutari from GUSTO00001.PersonelAvans pek inner join GUSTO00001.PersonelAvansTanimlari ek on pek.pAvansTanimi=ek.PersonelAvansTanimlari_rowid where  pek.ldAvansTutari>0	
GO