create view [GUSTO00005].[V_PersonelGorevOrganizasyon]  As  SELECT K.PersonelKodu_rowid AS PersId, K.sKod AS PersKod,  K.sAd AS PersAd, G.sGorevTanimKodu AS GorevKod,  G.sGorevTanimAdi AS GorevAd, O.sKod AS OrgKod,  O.sAd AS OrgAd, K.pPersonelDetayi, K.sSoyad AS PersSoyad,  Sendikalar.sSendikaKodu AS SendkKod,  Sendikalar.sSendikaAdi AS SendkAd,  PersonelSendika.dtGiris AS dtSenGiris,  PersonelSendika.dtAyrilis AS dtSenCikis FROM GUSTO00005.Sendikalar RIGHT OUTER JOIN GUSTO00005.PersonelSendika ON  Sendikalar.Sendikalar_rowid = PersonelSendika.pSendika RIGHT OUTER JOIN GUSTO00005.PersonelKodu K ON  PersonelSendika.pPersonel=K.PersonelKodu_rowid LEFT OUTER JOIN GUSTO00005.GorevTanimlari G RIGHT OUTER JOIN GUSTO00005.OrganizasyonKadro KD ON  G.GorevTanimlari_rowid=KD.pGorevTanimi LEFT OUTER JOIN GUSTO00005.Organizasyon O ON  KD.pOrganizasyon=O.Organizasyon_rowid RIGHT OUTER JOIN GUSTO00005.PersonelGorevYerleri GY ON  KD.OrganizasyonKadro_rowid=GY.pKadro ON  K.PersonelKodu_rowid=GY.pPersonel
